    public static ArrayList<Kuliner> getListData(){
        ArrayList<Kuliner> list = new ArrayList<>();
        for (String[] aData : data) {
            Kuliner kuliner = new Kuliner();
            kuliner.setName(aData[0]);
            kuliner.setDeskripsi(aData[1]);
            kuliner.setContent(aData[2]);
            kuliner.setPhoto(aData[3]);
            list.add(kuliner);
        }
        return list;
    }

